CREATE TABLE IF NOT EXISTS `wp_ori_task_action` (
`id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
`task_id`  int(10) NULL  COMMENT '任务',
`project_id`  int(10) NULL  COMMENT '项目',
`start_time`  int(10) NULL  COMMENT '开始时间',
`end_time`  int(10) NULL  COMMENT '结束时间',
`team_id`  int(10) NULL  COMMENT '队伍',
`token`  varchar(255) NULL  COMMENT 'token',
`uid`  int(10) NULL  COMMENT '用户UID',
`openid`  varchar(255) NULL  COMMENT 'OpenId',
PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci CHECKSUM=0 ROW_FORMAT=DYNAMIC DELAY_KEY_WRITE=0;
UPDATE `wp_attribute` a, wp_model m SET a.model_id = m.id WHERE a.model_name=m.`name`;

